function index(options = {}) {
  const { prefix = "" } = options;
  return {
    type: "component",
    resolve: (name) => {
      if (name.startsWith(prefix)) {
        const componentName = name.substring(prefix.length);
        if (Object.values(components).flat().includes(componentName)) {
          return {
            name: componentName,
            from: "radix-vue"
          };
        }
      }
    }
  };
}

export { index as default };
